// CONSTS
const sun = parcel.getFeatureById('sun');
const fallSound = parcel.getFeatureById('fall-sound');
const laughterSound = parcel.getFeatureById('laughter-sound');
const yowSound = parcel.getFeatureById('yow-sound');
const bombSound = parcel.getFeatureById('bomb-sound');
const boomBomb = parcel.getFeatureById('boom-bomb');
const boomShotgun = parcel.getFeatureById('boom-shotgun');
const ufoBomb = parcel.getFeatureById('ufo-bomb');
const states = [
  {
    x: -2,
    z: 3
  },
  {
    x: -1,
    z: 3
  },
  {
    x: 0,
    z: 3
  },
  {
    x: 1,
    z: 3
  },
  {
    x: 2,
    z: 3
  },
]
const ufoY = 2;
const boomShotgunY = 2.65;
const rotateAnimation = feature.createAnimation('rotation');
rotateAnimation.setKeys([{
  frame: 60,
  value: feature.rotation.add(new Vector3(0, feature.rotation.y + Math.PI * 2, 0))
}]);

// VARS
let currentIndex = 1;
let rotateInterval = null;
let laughInterval = null;
let lives = 5;
let dead = false;

// initial position
feature.set({position: [states[currentIndex].x, -1, states[currentIndex].z]});
ufoBomb.position.y = -5;
boomBomb.position.y = -5;

feature.shoot = function(position) {
  if (dead || (position !== currentIndex - 2)) return;
  let index = -1;
  while (index < 0 || index === currentIndex) {
    index = getRandomInt(0, 5);    
  }
  laughterSound.stop();
  lives--;
  boomShotgun.set({position: [feature.position.x, boomShotgunY, feature.position.z-0.7]});
  if (lives <= 0) {
    dead = true;
    feature.exit();
  } else {
    yowSound.play();      
    moveTo(index);
    setTimeout(() => {
      hideBoomShotgun();
    }, 500);
  }
}

function rotate() {
  if (rotateInterval ) {
    clearInterval(rotateInterval );
    rotateInterval = null;  
    return;
  }
  rotating = true;

  rotateAnimate();
  bomb();
  rotateInterval = setInterval(() => {
      rotateAnimate();
      bomb();
  }, 2000);
  laughterSound.play();
}

function rotateAnimate() {
    feature.startAnimations([rotateAnimation]);
}

function moveTo(index) {
  if (currentIndex === index) return;
  const currentState = states[currentIndex];
  const newState = states[index];
  if (!newState) return;  
  currentIndex = index;
  const deltaX = newState.x - currentState.x;
  const deltaZ = newState.z - currentState.z;
  const moveAnimation = feature.createAnimation('position');
  moveAnimation.setKeys([{
    frame: 30,
    value: feature.position.add(new Vector3(deltaX, 0, deltaZ))
  }]);
  // suspend solo rotation
  rotate();
  feature.startAnimations([moveAnimation, rotateAnimation ]);
  setTimeout(() => {
    // bring back solo rotation  
    rotate();  
  }, 1000);
  // fix position change
  feature.set({position: [newState.x, feature.position.y, newState.z]});
}

function bomb() {
  bombSound.stop();
  const pos = feature.position;
  ufoBomb.set({position: [pos.x, pos.y, pos.z]});
  const deltaX = sun.position.x - pos.x;
  const deltaY = sun.position.y - pos.y;
  const deltaZ = sun.position.z - pos.z;
  const moveAnimation = feature.createAnimation('position');
  moveAnimation.setKeys([{
    frame: 30,
    value: feature.position.add(new Vector3(deltaX, deltaY + 1, deltaZ))
  }]);
  ufoBomb.startAnimations([moveAnimation]);
  setTimeout(() => {
    bombSound.play();
    ufoBomb.position.y = -5;
    const bombX = (currentIndex - 2) * 0.1;
    boomBomb.set({position: [bombX, 5, boomBomb.position.z]});
    setTimeout(() => {
      boomBomb.position.y = -5;
    }, 500)
  }, 1000)
}

function getRandomInt(min, max) {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min) + min); //The maximum is exclusive and the minimum is inclusive
}

function hideBoomShotgun() {
  boomShotgun.position.y = -5;
}

feature.enter = function() {
  const newState = states[currentIndex];
  const deltaY = ufoY - feature.position.y;
  const moveAnimation = feature.createAnimation('position');
  moveAnimation.setKeys([{
    frame: 30,
    value: feature.position.add(new Vector3(0, deltaY, 0))
  }]);
  feature.startAnimations([moveAnimation, rotateAnimation]);
  setTimeout(() => {
    // fix position change
    feature.set({position: [newState.x, ufoY, newState.z]});
    // bring back solo rotation  
    rotate();
  }, 1000);
}

feature.exit = function() {
  const state = states[currentIndex];
  const deltaY = -1 - ufoY;
  const moveAnimation = feature.createAnimation('position');
  moveAnimation.setKeys([{
    frame: 120,
    value: feature.position.add(new Vector3(0, deltaY, 0))
  }]);
  const rotateAnimationLong = feature.createAnimation('rotation');
  rotateAnimationLong.setKeys([{
    frame: 120,
    value: feature.rotation.add(new Vector3(0, feature.rotation.y + Math.PI * 4, 0))
  }]);
  // suspend solo rotation
  rotate();
  feature.startAnimations([moveAnimation, rotateAnimationLong]);
  boomShotgun.startAnimations([moveAnimation]);
  fallSound.play();
  setTimeout(() => {
    // fix position change
    feature.set({position: [state.x, -1, state.z]});
    sun.nextSection();
  }, 4000);
}

hideBoomShotgun();

// constants
const ORIGIN_X = 0;
const ORIGIN_Y = 5;
const ORIGIN_Z = 4;
const planetNames = ['mercury', 'venus', 'earth', 'mars', 'jupiter', 'saturn', 'saturn-ring', 'uranus', 'neptune'];
const planets = planetNames.map(name => parcel.getFeatureById(name));
