const shotgun = parcel.getFeatureById('shotgun');
const buttonNext = parcel.getFeatureById('button-next');
const buttonStart = parcel.getFeatureById('button-start');
const buttonShotgun = parcel.getFeatureById('button-shotgun');
const attributions = parcel.getFeatureById('attributions');

feature.definition = {
  period: 0,
  r: 0, 
  rotation: 200
}
feature.state = {
  moving: false
}

// initial movement values
let sectionIndex = 0;
let orbitation = Math.PI/2;

feature.initPlanet = function(planet) {
  //resize
  const scale = Math.pow(planet.definition.size, 1/2.5)/2;
  planet.scale.x = scale;
  planet.scale.y = scale;
  planet.scale.z = scale;
  // reposition
  planet.position.x = planet.definition.r;
  planet.position.y = ORIGIN_Y + planet.definition.y;
  planet.position.z = ORIGIN_Z;
}

feature.toggleSystem = function() {
  clearPolytext();
  rotate(feature);
  planets.forEach(planet => {
    if (!planet.definition) return;
    move(planet);
  });
  buttonStart.hide();
  setTimeout(() => feature.nextSection(), 3000);
};

function move(planet) {
  if (planet.state.moving) {
    clearInterval(planet.state.orbitInterval);
    rotateStop(planet);
    planet.state.moving = false;  
  } else {
    orbit(planet);
    rotate(planet);
    planet.state.moving = true;
  }
}

function orbit(planet) {
  planet.state.orbitInterval = setInterval(() => {
    orbitation += 1.5; // arbitrary orbitation speed
    let angle = orbitation;
    if (planet.definition.period) {
      const period = 30 * Math.sqrt(planet.definition.period); // smaller difference in orbit for politeness
      angle = orbitation / period;
    }
    angle += Math.PI/2; // starting position  
    let newX = Math.sin(angle) * planet.definition.r;
    let newZ = Math.cos(angle) * planet.definition.r;
    planet.position.x = ORIGIN_X + newX;
    planet.position.z = ORIGIN_Z + newZ;
  }, 30);
}

function rotate(planet) {
  let speed = planet.definition.rotation * 10 || 300;
  speed = speed * 6; // modify speed for politeness
  rotateAnimation(planet, speed);
  planet.state.rotateInterval = setInterval(() => {
      rotateAnimation(planet, speed);
  }, speed);
}

function rotateAnimation(planet, speed) {
    let animation = planet.createAnimation('rotation');
    let frames = 30 / (1000 / speed);
    animation.setKeys([{
      frame: frames,
      value: planet.rotation.add(new Vector3(0, planet.rotation.y + Math.PI * 2, 0) )
    }]);
    planet.startAnimations([animation]);
}

function rotateStop(planet) {
  clearInterval(planet.state.rotateInterval);
}

