function setPolyText(id, text) {
  const feat = parcel.getFeatureById(id)
  if (text) {
    let length = text.length;
    let count = 0;
    const intervalId = setInterval(() => {
      const newText  = text.substring(0, count);
      feat.set({text:newText});
      count++;  
      if (count > length) clearInterval(intervalId);
    }, 80);
  } else {
    feat.set({text:''});
  }
}

function clearPolytext() {
