function createVox(position) {
  let vox = parcel.createFeature('vox-model');
  vox.set({
      url: 'https://media-crvox.sfo2.digitaloceanspaces.com/0x517dcd3ed115f0139fb2d89dffd0593641722390/1631169704473-f58667a2-7fc4-42f6-a4cc-42f4ab8ea202.vox',
      position: [position - 1, 1, 2],
      scale: [1, 1, 1]
  });
  return vox;
}

function cloneVox() {
  let template = parcel.getFeatureById('bullet-template');
  let vox = template.clone();
  const posX = 0 + currentPosition * 0.2;
  vox.set({
      position: [posX, 2, -1]
  });
  let moveAnimation = vox.createAnimation('position');
  const pX = getRandom(0, 0.5) - 0.25 + currentPosition * 0.6;
  const pY = getRandom(0, 1);
  moveAnimation.setKeys([{
    frame: 15,
    value: vox.position.add(new Vector3(pX, pY, 3))
  }]);  
  let rotateAnimation = vox.createAnimation('rotation');
  const rX = getRandom(1, 3) * Math.PI;
  const rY = getRandom(1, 3) * Math.PI;
  const rZ = getRandom(1, 3) * Math.PI;
  rotateAnimation.setKeys([{
    frame: 15,
    value: vox.rotation.add(new Vector3(rX, rY, rZ))
  }]);
  vox.startAnimations([moveAnimation, rotateAnimation]);
  return vox;
}

let currentPosition = 0;
let rotating = false;
feature.rotate = function(positionDelta) {
  if (rotating) return;
  let newPosition = currentPosition + positionDelta;
  if (newPosition < -2 || newPosition > 2) return;
  rotating = true;
  currentPosition = newPosition;
  let rotateAnimation = feature.createAnimation('rotation');
  const rY = positionDelta * (Math.PI / 16);
  rotateAnimation.setKeys([{
    frame: 15,
    value: feature.rotation.add(new Vector3(0, rY, 0))
  }]);
  feature.startAnimations([rotateAnimation]);
  setTimeout(() => {
    feature.rotation.y += rY;
    rotating = false;
  }, 500);
}

function getRandom(start, interval) {
  return start + interval * Math.random();
}

